//*******************
//**** REDUCERS *****
//*******************
//IMPORT COMBINE FUNCTION FROM REDUX
import { combineReducers } from 'redux'

const addDataReducer = (data = [], action) => {
    console.log(action.payload)     
    switch (action.type) {
      case 'ADD_DATA':
        return [...data, action.payload]
      case 'DELETE_DATA':
          return data.filter(data=>data.sno !== action.payload.sno.sno)
      default:
        return data
    }
  }

  export const updateUserSuccess = (data) => {
    return {
        type: UPDATE_USER,
        payload: {
            _id: data._id,
            name: data.name,
            cpfcnpj: data.cpfcnpj
        }
    }
}

export const updateUser = (id, name, cpfcnpj) => {
    console.log(id, name, cpfcnpj)
    return (dispatch) => {
        return axios.put(`${apiUrl}/users/${id}`, { name, cpfcnpj })
            .then(response => {
                dispatch(updateUserSuccess(response.data.data))
            })
            .catch(error => {
                throw (error);
            });
    };
}

  //EXPORT ALL REDUCERS AS A SINGLE INSTANCE SO IT CAN BE PASSED TO STORE OBJECT
export default combineReducers({
    addDataReducer
  })
