import uuid from 'uuid'

//********************
//**** ACTIONS *******
//********************

//ACTION CREATOR HELPER FUNCTION
const action = (type, payload) => {
    return { type, payload }
}

//ACTIONS
const ADD_DATA = 'ADD_DATA';
const DELETE_DATA = 'DELETE_DATA';
const UPDATE_DATA = 'UPDATE_DATA';

//ACTION CREATORS
export const addData = (sno, versionName, startDate, releaseDate, description) =>
    action(ADD_DATA, {
        key: uuid(),
        sno,
        versionName,
        startDate,
        releaseDate,
        description
    })

export const deleteData = (sno) => action(DELETE_DATA, { sno })

export const updateUserSuccess = (data) => {
    return {
        type: UPDATE_DATA,
        payload: {
            sno: data.sno,
            versionName: data.versionName,
            startData: data.cpfcnpj
        }
    }
}

export const updateUser = (id, name, cpfcnpj) => {
    console.log(id, name, cpfcnpj)
    return (dispatch) => {
        return axios.put(`${apiUrl}/users/${id}`, { name, cpfcnpj })
            .then(response => {
                dispatch(updateUserSuccess(response.data.data))
            })
            .catch(error => {
                throw (error);
            });
    };
}
